Greedy.prototype.solve = function (A, C, F) {
    var self = this;
    var L = self.min_degree_assignment(A, C);
    var result = Infinity;
    var cost = 0;
    var costs = [];
    
    for (var i in L) {
        costs = [];
        
        for (var f in F) {
            cost = 0;
            for (var j=0; j<A.length; j++) {
                if ((A[j] != 0) && (Math.abs(F[f]-A[j]) < C[L[i]][j])) {
                    cost += C[L[i]][j] - (Math.abs(F[f]-A[j]) * C[L[i]][j]);
                }
            }
            if (result >= cost) {
                result = cost;
                A[L[i]] = F[f];
            }
            costs.push(cost)
        }
    }
    return result;
};